#pragma mark - Table view data source

- (NSInteger)numberOfSectionsInTableView:(UITableView *)tableView {
    return 1;
}

- (NSInteger)tableView:(UITableView *)tableView numberOfRowsInSection:(NSInteger)section {
    return self.data.count;
}
-(CGFloat)tableView:(UITableView *)tableView heightForRowAtIndexPath:(NSIndexPath *)indexPath{
    DetailModel *model = self.data[indexPath.row];
    if (model.cellHeight == 0) {
        CGFloat height = [DetailCell heightOfObject:model];
        model.cellHeight = height;
        [self.data replaceObjectAtIndex:indexPath.row withObject:model];
    }
    return model.cellHeight;
}
-(void)tableView:(UITableView *)tableView willDisplayCell:(UITableViewCell *)cell forRowAtIndexPath:(NSIndexPath *)indexPath{
    DetailModel *model = self.data[indexPath.row];
    DetailCell *displayCell = (DetailCell *)cell;
    [displayCell showCellWithModel:model];
}
- (UITableViewCell *)tableView:(UITableView *)tableView cellForRowAtIndexPath:(NSIndexPath *)indexPath {
    DetailCell *cell = [tableView dequeueReusableCellWithIdentifier:@"DetailCell" forIndexPath:indexPath];
    return cell;
}


